import logging
import random
import time
from fastapi import FastAPI, Request
from datetime import datetime
from fastapi.responses import RedirectResponse
from fastapi.templating import Jinja2Templates
from fastapi.staticfiles import StaticFiles
from pathlib import Path



# 로깅 설정
logging.basicConfig(
    level=logging.INFO,
    format="%(asctime)s - %(name)s - %(levelname)s - %(message)s",
    handlers=[
        logging.FileHandler("/var/log/fastapi/app.log"),
        logging.StreamHandler()
    ]
)

logger = logging.getLogger("fastapi-app")

app = FastAPI()

# Jinja2 템플릿 설정 (템플릿 디렉토리 경로 지정)
templates = Jinja2Templates(directory="/app/templates")


@app.middleware("http")
async def log_requests(request: Request, call_next):
    start_time = time.time()

    # 요청 로깅
    logger.info(f"Request started: {request.method} {request.url.path}")

    response = await call_next(request)

    # 응답 시간 계산
    process_time = time.time() - start_time
    logger.info(f"Request completed: {request.method} {request.url.path} - Took: {process_time:.4f}s")

    return response

@app.get("/")
async def root():
    logger.info("Root endpoint called")
    return {"message": "Hello World"}

@app.get("/health")
async def health():
    logger.info("Health check endpoint called")
    return {"status": "healthy", "timestamp": datetime.now().isoformat()}

@app.get("/error")
async def trigger_error():
    logger.error("Error endpoint called - Generating sample error")
    return {"error": "This is a sample error log message"}


@app.get("/foodRec")
async def food_recommendation():
    # 3으로 나누어 랜덤으로 추천 (0, 1, 2 중 하나를 선택)
    recommendation = random.randint(0, 2)
    logger.info(f"Food recommendation result: {recommendation}")
    
    # 나눈 결과에 따라 리디렉션
    if recommendation == 0:
        return RedirectResponse(url="/chicken")
    elif recommendation == 1:
        return RedirectResponse(url="/pizza")
    else:
        return RedirectResponse(url="/hamburger")

# 각 음식에 대한 엔드포인트 (chicken, pizza, hamburger)
@app.get("/chicken")
async def chicken(request: Request):
    logger.info(f"Food recommendation result: chicken ")
    image_url = "data:image/jpeg;base64,"  # 외부 URL (이미지 URL은 실제로 존재하는 외부 URL로 바꿔야 함)
    return templates.TemplateResponse("food.html", {"request": request, "food": "chicken", "image_url": image_url})

@app.get("/pizza")
async def pizza(request: Request):
    logger.info(f"Food recommendation result: pizza ")
    image_url = "data:image/jpeg;base64,"  # 외부 URL (이미지 URL은 실제로 존재하는 외부 URL로 바꿔야 함)
    return templates.TemplateResponse("food.html", {"request": request, "food": "pizza", "image_url": image_url})

@app.get("/hamburger")
async def hamburger(request: Request):
    logger.info(f"Food recommendation result: hamburger ")
    image_url = "data:image/jpeg;base64,"  # 외부 URL (이미지 URL은 실제로 존재하는 외부 URL로 바꿔야 함)
    return templates.TemplateResponse("food.html", {"request": request, "food": "hamburger", "image_url": image_url})
